#include "Carbon.r"#include "QuickDraw.r"resource 'STR#' (17152, "Vtruncate") {	{	/* array StringArray: 3 elements */		/* [1] */		"Removes the fractional part of the input"		".";		/* [2] */		"List input";		/* [3] */		"List output"	}};resource 'vers' (1, "Vtruncate") {	0x1,	0x5,	release,	0x0,	0,	"1",	"Removes the fractional part of the input"};resource 'vers' (2, "Vtruncate") {	0x1,	0x5,	release,	0x0,	0,	"1",	"©2000 by Schiphorst, Jaffe, Gregory and "	"Gregson."};